mapper.select:
select

from bdc_biz_type_convert_field
where biz_type = #{bizType,jdbcType=VARCHAR}
and field = #{field,jdbcType=VARCHAR}
and field_key = #{fieldKey,jdbcType=VARCHAR}

mapper.delete:
delete from bdc_biz_type_convert_field
where biz_type = #{bizType,jdbcType=VARCHAR}
  and field = #{field,jdbcType=VARCHAR}
  and field_key = #{fieldKey,jdbcType=VARCHAR}

mapper.update:
update bdc_biz_type_convert_field


        field_value = #{fieldValue,jdbcType=VARCHAR},


where biz_type = #{bizType,jdbcType=VARCHAR}
and field = #{field,jdbcType=VARCHAR}
and field_key = #{fieldKey,jdbcType=VARCHAR}

update bdc_biz_type_convert_field
set field_value = #{fieldValue,jdbcType=VARCHAR}
where biz_type = #{bizType,jdbcType=VARCHAR}
  and field = #{field,jdbcType=VARCHAR}
  and field_key = #{fieldKey,jdbcType=VARCHAR}

